package advent.day2

class Key(val x: Int, val y: Int) {

	val number: String get() = (y * 3 + x + 1).toString()

    fun moveX(step: Int) = Key(clamp(x + step), y)

	fun moveY(step: Int) = Key(x, clamp(y + step))

    fun press(input: String): Key {
		return input.fold(this, { k, c ->
			when (c) {
				'U' -> k.moveY(-1)
				'D' -> k.moveY(1)
				'L' -> k.moveX(-1)
				'R' -> k.moveX(1)
				else -> throw IllegalArgumentException("Derp: $c")
			}
		})
	}

	override fun toString(): String = "Key $number"

	private fun clamp(v: Int) = Math.min(2, Math.max(v, 0))
}


fun enterCode(input: String): String {
    var key = Key(1, 1)
	var code = ""
	for (s in input.split("\\s".toRegex())) {
		key = key.press(s)
		code += key.number
	}
	return code
}

fun main(args: Array<String>) {
    var code = """
		|ULL
		|RRDDD
		|LURDL
		|UUUUD""".trimMargin()

	code = """
		|
		|
		|
		|
		|
		""".trimMargin()

    println(enterCode(code))
}
